import React from 'react'
import { useState } from 'react';
import { Form, Page, FormLayout, Stack, TextField, Button, Layout } from '@shopify/polaris'

const Register = () => {
  const [name, setName ] = useState('');
  const [email, setEmail ] = useState('');
  const [representated, setRepresentated ] = useState('');
  const [creditCard, setCreditCard ] = useState('');
  const [phone, setPhone ] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    alert(`Datos ${nameStore} ${email} ${nameRepresentated} ${creditCard} ${phone}`);
  }
  
  return (
    <Page fullWidth>
      <Form onSubmit={handleSubmit}>
        <FormLayout> 
          <Layout>          
          <Layout.Section oneHalf>
              <TextField
                  value={name}
                  onChange={(value) => setName(value)}
                  label="Nombre de la tienda"
                  type="text"            
              />
              <TextField
                  value={email}
                  onChange={(value) => setEmail(value)}
                  label="Email"
                  type="email"            
              />
            <TextField
              value={representated}
              onChange={(value) => setRepresentated(value)}
              label="Nombre del representante legal"
              type="text"            
          />
          </Layout.Section>         
          <Layout.Section oneHalf>
            <TextField
              value={creditCard}
              onChange={(newValue) => setCreditCard(newValue)}
              label="Opcional (Tarjeta de crÃ©dito)"
              type="text"            
            />
            <TextField
              value={phone}
              onChange={(value) => setPhone(value)}
              label="Telefono"
              type="text"            
            />
            <Stack>
              <Button primary submit>Guardar</Button>
            </Stack>
          </Layout.Section>         
          </Layout>         
        </FormLayout>
      </Form>
    </Page>
  );
}


export default Register;
