import { Page, 
    Card, 
    Avatar, 
    FooterHelp, 
    Link, 
    ResourceList, 
    ResourceItem,
    Badge,
    TextStyle,
    Stack } from '@shopify/polaris'
import { useEffect } from 'react'

const Products = ({
    variant_is_loading,
    variants
}) => {
    //llamada a api interna para encontrar id
    useEffect(() => {
  
        getShopifyData()
    
      },[])

      const resourceName = {
          singular: 'variant',
          plural: 'variants'
      }

      const renderItem = (item) => {
          const {
              _id: id,
              product_title: title,
              product_image: image_url,
              final_price = 0,
              status,
              final_duty = 0
          } = item

          let statusColor = null
          let columnA = null
          let columnB = null

          switch(status) {
              case 'Calculando':
                  statusColor = 'attention';
                  break;
              case 'Sin Conflicto':
              case 'Completo':
                  statusColor = 'succes';
                  columnA = `$${final_price} Subtotal`
                  columnB = `$${final_duty} Impuestos`
                  break;
              case 'Conflicto':
                  statusColor = 'warning';
                  break;
          }

          const media = <Avatar customer size="medium" source={image_url} />

          return (
              <ResourceItem id={ id }>
                  <Stack>
                      <Stack.Item fill>
                        <h3><TextStyle variation="strong">{title} - {variant_title}</TextStyle></h3>
                      </Stack.Item>

                      <Stack.Item>
                          {columnA}
                      </Stack.Item>

                      <Stack.Item>
                          {columnB}                          
                      </Stack.Item>
                      <Stack.Item>
                          <Badge  status={statusColor}>{status}</Badge>
                      </Stack.Item>
                  </Stack>
              </ResourceItem>
          )
      }

    return (
        <Page fullWidth>
            <Card>
                <ResourceList 
                    resourceName = { resourceName }
                    items = { variants }
                    showHeader = { false }
                    loading = { variant_is_loading}
                    renderItem={renderItem}
                />
            </Card>

            <FooterHelp>
                Soy Marcos {' '}
                <Link url="https://help.shopify.com/manual/orders/fulfill-orders">
                    GÃ³mez
                </Link>
            </FooterHelp>

        </Page>
    )
}

export default Products
