// import { TextStyle } from '@shopify/polaris'
import { 
    Card, 
    Page, 
    Layout,
    Button,
    Form,
    FormLayout,
    Stack,
    TextField 
} from '@shopify/polaris';
import { useState } from 'react';

const handleSubmit = (e) => {
    console.log(e);
}

const handleChange = (valor)=> {
    console.log(valor);
    setDescuento(valor);
}

const OtroLayout = () => {
    const [descuento, setDescuento] = useState('10%');
    return(
        <Page>
            <Layout>
                <Layout.AnnotatedSection
                    title='Algún otro título'
                    description='Alguna otra descripción'
                >
                    <Card sectioned>
                        <Form onSubmit={handleSubmit}>
                            <FormLayout>
                                <TextField
                                    value={descuento}
                                    onChange={handleChange}
                                    label='Descuento'
                                    type='discount'
                                />
                                <Stack distribution='trailing'>
                                    <Button primary submit>
                                        Guardar
                                    </Button>
                                </Stack>
                            </FormLayout>
                        </Form>
                        <div>Hola soy un card</div>
                    </Card>

                </Layout.AnnotatedSection>
            </Layout>
        </Page>
    )
}

    

export default OtroLayout;
