                        </Stack>
                    </Card>
                </Layout.Section>
                <Layout.Section secondary>
                    <Card sectioned>                        
                    <Button fullWidth={true} url="/register">Registro</Button>
                    <Button fullWidth={true} onClick={() => setOpen(true)}>Enviar productos a revisión</Button>                                                               
                    </Card>
                </Layout.Section>
                {/* <Layout.Section secondary>
                    <EmptyState
                        heading="Soy un titulo"
                        action={{
                            content: 'Click on me',
                            onAction: ()=> setOpen(true)
                        }}
                        image={img}
                    >
                        <p>Selecciona productos</p>
                    </EmptyState>
                </Layout.Section>
                <Layout.Section secondary>                    
                    <EmptyState
                        heading="Soy un titulo"
                        action={{
                            content: 'Click on me',
                            onAction: ()=> setOpen(true)
                        }}
                        image={img}
                    >
                        <p>Enviar productos a revision</p>
                    </EmptyState>
                </Layout.Section> */}
            </Layout>



            <FooterHelp>
            Soy Marcos{' '}
            <Link url="https://help.shopify.com/manual/orders/fulfill-orders">
                Gómez
            </Link>            
            </FooterHelp>
        </Page>    
    )
}


export default Index
