import { Modal, 
    Stack, 
    Thumbnail, 
    TextContainer, 
    Card, 
    Button, 
    Pagination} 
from '@shopify/polaris'

import { useState, useEffect } from 'react'

const ResolveConflict = ({
    variants_with_conflict,
    variant_in_modal,
    can_solve,
    modal_open,

    closeModal,
    stepVariant
})=>{


    const {
        id,
        product_image: image_url,
        product_title: title,
        variant_price: price,
        variant_recommended_price: recommended_price,
        tax_calculated: calculated_duty = 0,
        calculated_duty_original = 0,
        priced_selected
    } = variant_in_modal

    let pagination = null
    if(variants_with_conflict.length > 0) {
        pagination = (
            <Pagination
                hasPrevious={true}
                onPrevious={() => {
                    console.log('Previous')
                }}
                hastNext={true}
                onNext={() => {
                    console.log('Next')
                }}
            />
        )
    }

    const handleSubmit = () => {

    }    
    
    const handleClose = () => {
        closeModal()
    }

    const handleSelectPrice = () => {

    }

    const footer = (
        <Stack>
            <Stack.Item fill>
                {pagination}
            </Stack.Item>
            
            <Stack.Item>
                <Button primary onClick={handleSubmit} disabled={ !can_solve }>Guarda mi selección</Button>
            </Stack.Item>
        </Stack>
    )
    

    return (
        <Modal
        large
        open={modal_open}
        onClose={handleClose}
        title={`Resolver conflicto`}
        footer={footer}
        >
            <Modal.Section>

            <TextContainer>
                <div>Nuestros expertos sugieren diferentes impuestos de los que usted ha indicado. Seleccione cual es el correcto</div>
                <Stack distribution="fillEvenly">
                    <Stack.Item>
                        <Stack>
                            <Thumbnail 
                            source={image_url}
                            />
                            <div>{title}</div>
                        </Stack>                        
                    </Stack.Item>
                    <Stack.Item>
                        <div>Regular price: ${price}</div>
                        <div>Impuestos: selecciona a continuación</div>
                    </Stack.Item>
                </Stack>
                <Stack distribution="fillEvenly">
                    <Card sectioned>
                        <div className="title-wrapper">
                            <div className="subtitle">Tu ingresaste</div>
                        </div>
                        <div className="title-price">${price}</div>
                        <p className="text-center">Impuesto ${calculated_duty_original}</p>
                        <div className="text-center mt-10">
                            <Button 
                                onClick={() => handleSelectPrice}
                                primary={false}                        
                            >
                                Seleccionar
                            </Button>
                        </div>
                    </Card>
                    <Card sectioned>
                        <div className="title-wrapper">
                            <div className="subtitle">Nosotros recomendamos</div>
                        </div>
                        <div className="title-price">${recommended_price}</div>
                        <p className="text-center">Impuesto ${calculated_duty}</p>
                        <div className="text-center mt-10">
                            <Button 
                                onClick={() => handleSelectPrice}
                                primary={false}                        
                            >
                                Seleccionar
                            </Button>
                        </div>
                    </Card>                
                </Stack>
            </TextContainer>
            </Modal.Section>
            
        </Modal>
    )

}

export default ResolveConflict
