//Acciones puras no async
import types from './types'

const saveShopifyData = (shop)=>{
    return {
        type: types.SAVE_SHOPIFY_DATA,
        shop        
    }
}

const shopExists = ( data_store ) => {
    return {
        type: types.SHOP_EXISTS,
        data_store
    }
}

const shopNotExists = () => {
    return {
        type: types.SHOP_NOT_EXISTS
    }
}

export default {
    saveShopifyData,

}
