// Acciones con axios async
// Usamos thunk

import axios from 'axios';
import Creators from './actions';

// A =====================
// funciones que se usan internamente en este archivo
// pero que el componente no necesita ejecutara
const _saveShopifyData = Creators.saveShopifyData

// B ======================
// funciones que se conectan con apis
// async usan thunk
const getShopifyData = () => {
    return (dispatch) => {
        const instanceShopify = axios.create({
            baseURL: '/api',
            timeout: 5000
        })

        return instanceShopify.get(`/shopify`)
            .then(response => {
                
                const { shop } = response.data
                dispatch(_saveShopifyData(shop))

                const { id } = shop
                axios.get(`/store/${id}`)
                    .then (response => {

                    }, error => {
                        
                    })

            }, error => {

            })
    
    }
}

// C ======================
// exportar las funciones que finalmente se van a comunicar
// con los componentes reales, es decir tienen comunicación
// con el exterior
export default {
    getShopifyData
}
