import { Page, Avatar, FooterHelp, Link, ResourceList, ResourceItem } from '@shopify/polaris'
import { useEffect } from 'react'

const Products = ({
    variant_is_loading,
    variants
}) => {
    //llamada a api interna para encontrar id
    useEffect(() => {
  
        getShopifyData()
    
      },[])

      const resourceName = {
          singular: 'variant',
          plural: 'variants'
      }

      const renderItem = (item) => {
          const {
              _id: id,
              product_title: title,
              product_image: image_url,
              final_price = 0,
              status,
              final_duty = 0
          } = item

          const media = <Avatar customer size="medium" source={image_url} />

          return (
              <ResourceItem id={ id }>

              </ResourceItem>
          )
      }

    return (
        <Page fullWidth>

            <ResourcerList 
                resourceName = { resourceName }
                items = { variants }
                showHeader = { false }
                loading = { variant_is_loading}
                renderItem={renderItem}
            />

            <FooterHelp>
                Soy Marcos {' '}
                <Link url="https://help.shopify.com/manual/orders/fulfill-orders">
